// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface IRaffleFactory {
    function requestRandomnessForRaffle() external returns (uint256 requestId);
}

/**
 * @title Raffle
 * @notice Individual raffle contract with provably fair winner selection via Factory
 * @notice Supports multiple ticket purchases per wallet and permissionless winner drawing
 * @dev Requests randomness through RaffleFactory
 */
contract Raffle is ReentrancyGuard {
    // Raffle status enum
    enum RaffleStatus {
        UPCOMING,
        ACTIVE,
        ENDED,
        DRAWN,
        CANCELLED
    }

    // Raffle details
    string public title;
    string public description;
    string public prizeDescription;
    uint256 public entryFee;
    uint256 public deadline;
    uint256 public maxParticipants; // Max total tickets (0 = unlimited)
    address public creator;
    RaffleStatus public status;
    uint256 public creatorCommissionBps; // 100 = 1%, 1000 = 10%

    // Participants - stores ticket entries (same address can appear multiple times)
    address[] public participants; // Each entry = 1 ticket
    mapping(address => uint256) public ticketCount; // Tracks tickets per wallet

    // Winner selection
    address public winner;
    uint256 public winnerIndex;
    uint256 public randomResult;
    uint256 public vrfRequestId;

    // Factory address (handles VRF requests)
    IRaffleFactory public immutable factory;

    // Events
    event ParticipantJoined(address indexed participant, uint256 ticketsBought, uint256 totalTickets, uint256 participantTicketCount);
    event WinnerDrawn(address indexed winner, uint256 winnerIndex, uint256 randomNumber, uint256 vrfRequestId);
    event PrizeClaimed(address indexed winner, uint256 amount);
    event CreatorCommissionPaid(address indexed creator, uint256 amount);
    event RaffleCancelled();
    event RandomnessRequested(uint256 requestId);

    // Errors
    error NotCreator();
    error RaffleNotActive();
    error RaffleEnded();
    error InsufficientPayment();
    error RaffleFull();
    error DeadlineNotReached();
    error NoParticipants();
    error NotEnoughParticipants();
    error WinnerAlreadyDrawn();
    error NotWinner();
    error PrizeAlreadyClaimed();
    error InvalidTicketCount();
    error OnlyFactory();
    error DeadlineMustBeInFuture();
    error EntryFeeMustBePositive();
    error InvalidFactoryAddress();
    error MaxParticipantsTooHigh();
    error MinParticipantsTooLow();
    error EntryFeeTooHigh();
    error DeadlineTooFar();
    error TransferFailed();
    error InvalidRequestId();
    error WinnerAlreadySet();
    error InvalidCommission();

    /**
     * @notice Create a new raffle
     * @param _title Raffle title
     * @param _description Raffle description
     * @param _prizeDescription Prize description
     * @param _entryFee Entry fee per ticket in wei
     * @param _deadline Deadline timestamp
     * @param _maxParticipants Maximum total tickets (0 = unlimited)
     * @param _creator Creator address (passed from factory)
     * @param _factory Factory contract address
     * @param _creatorCommissionBps Creator commission in basis points (0-1000, i.e. 0%-10%)
     */
    constructor(
        string memory _title,
        string memory _description,
        string memory _prizeDescription,
        uint256 _entryFee,
        uint256 _deadline,
        uint256 _maxParticipants,
        address _creator,
        address _factory,
        uint256 _creatorCommissionBps
    ) {
        // Deadline validation
        if (_deadline <= block.timestamp) revert DeadlineMustBeInFuture();
        if (_deadline > block.timestamp + 365 days) revert DeadlineTooFar();

        // Entry fee validation
        if (_entryFee == 0) revert EntryFeeMustBePositive();
        if (_entryFee > 100 ether) revert EntryFeeTooHigh();

        // Max participants validation (prevent gas DoS and ensure at least 2 participants)
        if (_maxParticipants == 1) revert MinParticipantsTooLow();
        if (_maxParticipants > 10000) revert MaxParticipantsTooHigh();

        // Factory validation
        if (_factory == address(0)) revert InvalidFactoryAddress();

        // Commission validation (0-10%)
        if (_creatorCommissionBps > 1000) revert InvalidCommission();

        title = _title;
        description = _description;
        prizeDescription = _prizeDescription;
        entryFee = _entryFee;
        deadline = _deadline;
        maxParticipants = _maxParticipants;
        creator = _creator; // Use passed creator address instead of tx.origin
        status = RaffleStatus.ACTIVE;
        factory = IRaffleFactory(_factory);
        creatorCommissionBps = _creatorCommissionBps;
    }

    /**
     * @notice Buy multiple tickets at once
     * @param _ticketCount Number of tickets to purchase
     * @dev Same wallet can buy multiple tickets for better odds
     */
    function joinRaffle(uint256 _ticketCount) external payable {
        if (status != RaffleStatus.ACTIVE) revert RaffleNotActive();
        if (block.timestamp >= deadline) revert RaffleEnded();
        if (_ticketCount == 0) revert InvalidTicketCount();

        uint256 totalCost = entryFee * _ticketCount;
        if (msg.value < totalCost) revert InsufficientPayment();

        // Check max participants (if set)
        if (maxParticipants > 0) {
            if (participants.length + _ticketCount > maxParticipants) revert RaffleFull();
        }

        // Add each ticket as separate entry
        for (uint256 i = 0; i < _ticketCount; i++) {
            participants.push(msg.sender);
        }

        // Update ticket count for this wallet
        ticketCount[msg.sender] += _ticketCount;

        emit ParticipantJoined(msg.sender, _ticketCount, participants.length, ticketCount[msg.sender]);

        // Refund excess payment
        uint256 refund = msg.value - totalCost;
        if (refund > 0) {
            (bool success, ) = payable(msg.sender).call{value: refund}("");
            if (!success) revert TransferFailed();
        }
    }

    /**
     * @notice Draw winner using Chainlink VRF (via Factory)
     * @dev Can be called by anyone after deadline is reached or max participants is reached
     */
    function drawWinner() public returns (uint256 requestId) {
        // Allow anyone to draw after conditions are met
        if (block.timestamp < deadline && (maxParticipants == 0 || participants.length < maxParticipants)) {
            revert DeadlineNotReached();
        }
        if (participants.length == 0) revert NoParticipants();
        if (participants.length < 2) revert NotEnoughParticipants();
        if (winner != address(0)) revert WinnerAlreadyDrawn();

        status = RaffleStatus.ENDED;

        // Request randomness from factory
        requestId = factory.requestRandomnessForRaffle();
        vrfRequestId = requestId;

        emit RandomnessRequested(requestId);

        return requestId;
    }

    /**
     * @notice Receive random number from factory and select winner
     * @param _requestId VRF request ID
     * @param _randomWord Random number from Chainlink VRF
     */
    function fulfillRandomness(uint256 _requestId, uint256 _randomWord) external {
        if (msg.sender != address(factory)) revert OnlyFactory();
        if (_requestId != vrfRequestId) revert InvalidRequestId();
        if (winner != address(0)) revert WinnerAlreadySet();

        randomResult = _randomWord;
        winnerIndex = randomResult % participants.length;
        winner = participants[winnerIndex];
        status = RaffleStatus.DRAWN;

        emit WinnerDrawn(winner, winnerIndex, randomResult, _requestId);
    }

    /**
     * @notice Claim prize (winner only)
     * @dev Splits payout between winner and creator based on creatorCommissionBps
     */
    function claimPrize() external nonReentrant {
        if (msg.sender != winner) revert NotWinner();
        if (status != RaffleStatus.DRAWN) revert WinnerAlreadyDrawn();

        status = RaffleStatus.CANCELLED; // Prevent re-claiming
        uint256 prizeAmount = address(this).balance;

        // Calculate creator commission
        uint256 creatorAmount = (prizeAmount * creatorCommissionBps) / 10000;
        uint256 winnerAmount = prizeAmount - creatorAmount;

        emit PrizeClaimed(winner, winnerAmount);

        // Send winner their share
        (bool winnerSuccess, ) = payable(winner).call{value: winnerAmount}("");
        if (!winnerSuccess) revert TransferFailed();

        // Send creator their commission (if any)
        if (creatorAmount > 0) {
            emit CreatorCommissionPaid(creator, creatorAmount);
            (bool creatorSuccess, ) = payable(creator).call{value: creatorAmount}("");
            if (!creatorSuccess) revert TransferFailed();
        }
    }

    /**
     * @notice Cancel raffle and refund all participants (creator only, before drawing)
     */
    function cancelRaffle() external nonReentrant {
        bool isUnderfilled = block.timestamp >= deadline && participants.length < 2;
        if (msg.sender != creator && !isUnderfilled) revert NotCreator();
        if (status == RaffleStatus.DRAWN || status == RaffleStatus.CANCELLED) {
            revert RaffleEnded();
        }

        status = RaffleStatus.CANCELLED;
        emit RaffleCancelled();

        // Refund all participants
        uint256 refundPerTicket = entryFee;
        address[] memory participantsSnapshot = participants; // Gas optimization

        // Use ticketCount mapping for efficient refunds
        address lastRefunded = address(0);
        for (uint256 i = 0; i < participantsSnapshot.length; i++) {
            address participant = participantsSnapshot[i];

            // Only refund once per unique address
            if (participant != lastRefunded) {
                uint256 tickets = ticketCount[participant];
                uint256 refundAmount = tickets * refundPerTicket;
                (bool success, ) = payable(participant).call{value: refundAmount}("");
                if (!success) revert TransferFailed();
                lastRefunded = participant;
            }
        }
    }

    // View functions
    function getParticipants() external view returns (address[] memory) {
        return participants;
    }

    function getTotalTickets() external view returns (uint256) {
        return participants.length;
    }

    function getPrizePool() external view returns (uint256) {
        return address(this).balance;
    }

    function getUserTicketCount(address _user) external view returns (uint256) {
        return ticketCount[_user];
    }

    function getRaffleDetails() external view returns (
        string memory _title,
        string memory _description,
        uint256 _entryFee,
        uint256 _deadline,
        uint256 _maxParticipants,
        uint256 _currentParticipants,
        RaffleStatus _status,
        address _creator,
        address _winner,
        uint256 _creatorCommissionBps
    ) {
        return (
            title,
            description,
            entryFee,
            deadline,
            maxParticipants,
            participants.length,
            status,
            creator,
            winner,
            creatorCommissionBps
        );
    }
}

contracts/RaffleFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./Raffle.sol";
import "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title RaffleFactory
 * @notice Factory contract to create and manage multiple raffles
 * @dev Deploys new Raffle contracts and handles Chainlink VRF requests for all raffles
 */
contract RaffleFactory is VRFConsumerBaseV2Plus, Pausable {
    // Chainlink VRF Configuration
    bytes32 public immutable keyHash;
    uint256 public immutable subscriptionId;
    uint16 private constant REQUEST_CONFIRMATIONS = 3;
    uint32 private constant CALLBACK_GAS_LIMIT = 300000;
    uint32 private constant NUM_WORDS = 1;

    // Platform fee configuration
    address public immutable platformOwner;
    uint256 public constant CREATION_FEE_BPS = 100; // 1%
    uint256 public constant MIN_FEE = 0.0004 ether;
    uint256 public constant MAX_FEE = 0.05 ether;

    // All deployed raffles
    address[] public allRaffles;

    // Mapping from creator to their raffles
    mapping(address => address[]) public rafflesByCreator;

    // Mapping to check if address is a raffle
    mapping(address => bool) public isRaffle;

    // Mapping from VRF requestId to raffle address
    mapping(uint256 => address) public requestIdToRaffle;

    // Events
    event RaffleCreated(
        address indexed raffleAddress,
        address indexed creator,
        string title,
        uint256 entryFee,
        uint256 deadline,
        uint256 maxParticipants,
        uint256 creatorCommissionBps
    );

    event RandomnessRequested(address indexed raffle, uint256 indexed requestId);
    event RandomnessFulfilled(address indexed raffle, uint256 indexed requestId, uint256 randomWord);
    event FeesWithdrawn(address indexed owner, uint256 amount);

    // Errors
    error InsufficientCreationFee();
    error NotPlatformOwner();
    error WithdrawFailed();
    error TransferFailed();
    error OnlyRaffleContract();
    error InvalidRequestId();

    /**
     * @notice Initialize factory with Chainlink VRF configuration
     * @param _vrfCoordinator Chainlink VRF Coordinator address
     * @param _keyHash Chainlink VRF Key Hash
     * @param _subscriptionId Chainlink VRF Subscription ID
     */
    constructor(
        address _vrfCoordinator,
        bytes32 _keyHash,
        uint256 _subscriptionId
    ) VRFConsumerBaseV2Plus(_vrfCoordinator) {
        keyHash = _keyHash;
        subscriptionId = _subscriptionId;
        platformOwner = msg.sender;
    }

    /**
     * @notice Create a new raffle
     * @param _title Raffle title
     * @param _description Raffle description
     * @param _prizeDescription Prize description
     * @param _entryFee Entry fee in wei
     * @param _deadline Deadline timestamp
     * @param _maxParticipants Maximum participants (0 = unlimited)
     * @return raffleAddress Address of the newly created raffle
     */
    /**
     * @notice Calculate the creation fee for a raffle
     * @param _entryFee Entry fee per ticket in wei
     * @param _maxParticipants Maximum total tickets (0 = unlimited)
     * @return fee The creation fee in wei
     */
    function calculateCreationFee(uint256 _entryFee, uint256 _maxParticipants) public pure returns (uint256 fee) {
        if (_maxParticipants == 0) {
            return MAX_FEE;
        }
        fee = (_entryFee * _maxParticipants * CREATION_FEE_BPS) / 10000;
        if (fee < MIN_FEE) fee = MIN_FEE;
        if (fee > MAX_FEE) fee = MAX_FEE;
        return fee;
    }

    function createRaffle(
        string memory _title,
        string memory _description,
        string memory _prizeDescription,
        uint256 _entryFee,
        uint256 _deadline,
        uint256 _maxParticipants,
        uint256 _creatorCommissionBps
    ) external payable whenNotPaused returns (address raffleAddress) {
        // Check creation fee
        uint256 requiredFee = calculateCreationFee(_entryFee, _maxParticipants);
        if (msg.value < requiredFee) revert InsufficientCreationFee();

        // Create new raffle contract (pass creator address and factory address)
        Raffle newRaffle = new Raffle(
            _title,
            _description,
            _prizeDescription,
            _entryFee,
            _deadline,
            _maxParticipants,
            msg.sender, // Pass creator address (fixes tx.origin vulnerability)
            address(this), // Pass factory address
            _creatorCommissionBps
        );

        raffleAddress = address(newRaffle);

        // Store raffle
        allRaffles.push(raffleAddress);
        rafflesByCreator[msg.sender].push(raffleAddress);
        isRaffle[raffleAddress] = true;

        emit RaffleCreated(
            raffleAddress,
            msg.sender,
            _title,
            _entryFee,
            _deadline,
            _maxParticipants,
            _creatorCommissionBps
        );

        // Refund excess fee
        uint256 excess = msg.value - requiredFee;
        if (excess > 0) {
            (bool success, ) = payable(msg.sender).call{value: excess}("");
            if (!success) revert TransferFailed();
        }

        return raffleAddress;
    }

    /**
     * @notice Request randomness for a raffle (called by Raffle contracts)
     * @return requestId VRF request ID
     */
    function requestRandomnessForRaffle() external returns (uint256 requestId) {
        if (!isRaffle[msg.sender]) revert OnlyRaffleContract();

        // Request randomness from Chainlink VRF
        requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: keyHash,
                subId: subscriptionId,
                requestConfirmations: REQUEST_CONFIRMATIONS,
                callbackGasLimit: CALLBACK_GAS_LIMIT,
                numWords: NUM_WORDS,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({nativePayment: false})
                )
            })
        );

        // Store mapping
        requestIdToRaffle[requestId] = msg.sender;

        emit RandomnessRequested(msg.sender, requestId);

        return requestId;
    }

    /**
     * @notice Callback function used by VRF Coordinator
     * @param _requestId VRF request ID
     * @param _randomWords Array of random values
     */
    function fulfillRandomWords(uint256 _requestId, uint256[] calldata _randomWords) internal override {
        address raffleAddress = requestIdToRaffle[_requestId];
        if (raffleAddress == address(0)) revert InvalidRequestId();

        uint256 randomWord = _randomWords[0];

        // Forward the random number to the raffle
        Raffle(raffleAddress).fulfillRandomness(_requestId, randomWord);

        emit RandomnessFulfilled(raffleAddress, _requestId, randomWord);
    }

    // View functions
    function getAllRaffles() external view returns (address[] memory) {
        return allRaffles;
    }

    function getRafflesByCreator(address _creator) external view returns (address[] memory) {
        return rafflesByCreator[_creator];
    }

    function getRaffleCount() external view returns (uint256) {
        return allRaffles.length;
    }

    function getCreatorRaffleCount(address _creator) external view returns (uint256) {
        return rafflesByCreator[_creator].length;
    }

    /**
     * @notice Pause raffle creation (emergency mechanism, platform owner only)
     * @dev Existing raffles continue to function normally
     */
    function pause() external {
        if (msg.sender != platformOwner) revert NotPlatformOwner();
        _pause();
    }

    /**
     * @notice Unpause raffle creation (platform owner only)
     */
    function unpause() external {
        if (msg.sender != platformOwner) revert NotPlatformOwner();
        _unpause();
    }

    /**
     * @notice Withdraw accumulated creation fees (platform owner only)
     */
    function withdrawFees() external {
        if (msg.sender != platformOwner) revert NotPlatformOwner();
        uint256 balance = address(this).balance;
        (bool success, ) = platformOwner.call{value: balance}("");
        if (!success) revert WithdrawFailed();
        emit FeesWithdrawn(platformOwner, balance);
    }

    /**
     * @notice Get accumulated fees in the factory contract
     */
    function getAccumulatedFees() external view returns (uint256) {
        return address(this).balance;
    }
}

